insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('48443','ruRU','Когда-то наша цивилизация была великой. А что теперь?.. Спасибо тебе, $n. Совместными усилиями мы сумели вызволить из крепости Нат\'ракса немало узников. В цитадели осталось не так много рабов, да и Армия Света уже на подходе. Совсем скоро влияние Легиона в этой области значительно ослабнет.','0');
