insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('47330','ruRU','Это предостережение и впрямь не сулит нам ничего хорошего, $n. Похоже, судьбы Азерота и родного мира Легиона, Аргуса, неразрывно связаны.$B$BНам необходимо узнать о планах темного титана! Мне кажется, что связь Вестника с душой мира чрезвычайно важна.$B$BСохраняй бдительность, |3-6(Повелитель Пустоты). Мы должны довериться суждению Магни... и самого Азерота.','0');
