insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('47112','ruRU','Спасибо, $n. Это единственное, что мы можем сделать для тех, кто пожертвовал своей жизнью во имя Альянса... во имя Азерота.','0');
