insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42056','ruRU','Мы с тобой не первый день знакомы, $n. Не раз и не два твои решения приносили Роще Снов победу.$B$BЯ готов отдать свою жизнь, чтобы защитить Азерот, и знаю, что если придется, ты поступишь точно так же.$B$BВпереди нас по-прежнему ждет великая опасность. Прими мою клятву, Арсулус. Мы должны защитить природу!','0');
delete from `quest_offer_reward_locale` where `ID`=42055 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42055','ruRU','$n, мы, старейшины Рощи Снов, посовещались и единогласно пришли к соглашению. Мы хотим видеть тебя во главе Рощи Снов.$B$BИ сегодня присваиваем тебе титул Верховного друида Рощи Снов – высшее звание в нашем ордене.$B$BДолг нашего ордена – защищать природу, и ответственность за это отныне ложится на твои плечи! Будь достоин своего высокого звания, Арсулус.','0');
