insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42051','ruRU','Не обманывайся, $n. То, что кажется тебе горой Хиджал, на самом деле лишь наведенный врагом морок.$B$BЭто образ самого страшного кошмара из головы Малорна. Война древних, случившаяся тысячи лет назад.$B$BИменно во время этой войны Азерот впервые столкнулся с Пылающим Легионом. Именно в этой войне погиб Малорн и Дикие Боги.','0');
