insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42040','ruRU','Тут что-то не так, $n. Это не Изумрудный Сон. Неужели нашу священную землю наводнили твари Ксавия? Как им удалось пробиться через нашу защиту?','0');
