insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42039','ruRU','$n, моя жизнь принадлежит тебе. Ты подарил мне второй шанс, и я распоряжусь им достойно.$B$BБлагодаря тебе к нам вернулся Идол Леса. Страшно подумать, что эта священная реликвия могла попасть в руки наших врагов.$B$BТеперь я хочу довести дело до конца.$B$BПозволь мне сражаться вместе с тобой. Если кому и суждено победить Кошмар – то только тебе.','0');
