insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('42011','ruRU','Я очень благодарен тебе за спасение Азурегоса, $n. Теперь хоть смогу спать спокойно, зная, что он приглядит за Нексусом.$B$BПризнаться, ты очень удивил меня. Я за всю свою жизнь не встречал мага, способного контролировать хаотическую энергию Алунета. $B$BСпасибо за все, что ты сегодня сделал для меня и моих собратьев. Воистину, ты тот самый герой, который так нужен нам в битве с Легионом.','0');
