insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('41218','ruRU','Приятно познакомиться, $n. Наверное, ты слышал разговоры об операции \"Гномреган\". Позволь я немного расскажу тебе о том, что случилось, и почему мы оставили дворфийский город Стальгорн.','0');
delete from `quest_offer_reward_locale` where `ID`=41217 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('41217','ruRU','Для $R, проведшего в Гномрегане столько времени, у тебя определенно здоровый и цветущий вид, $n. Это хорошо. Чтобы завершить операцию "Гномреган", нам нужно собрать побольше таких охотников, как ты.','0');
