insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40901','ruRU','Мрачнокрыл был грозным демоном... Надо признать, твои навыки меня впечатляют, $n.$B$BНо предстоит убить еще гораздо больше демонов. Очень хорошо, что теперь со мной есть кто-то, на кого можно положиться.','0');
