insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40882','ruRU','<Хобарт забирает у вас механизм и начинает ковыряться со своим устройством.>$B$BДа, думаю, все получилось. Отличная работа, $n.$B$BДа, как всегда, ты можешь оставить мою схему себе и воспользоваться ей в своих целях.','0');
delete from `quest_offer_reward_locale` where `ID`=40877 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40877','ruRU','Ну что, $n, пожелай мне удачи. Надеюсь, все получится.$B$B<Хобарт забирает у вас руну и направляет ее энергию на детали для сборки ружья.>$B$BДа! ДА! Оно ожило! ОНО ОЖИЛО!$B$BТеперь я смогу клепать детали для ружей одну за другой, прямо как детские игрушки!','0');
