insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40740','ruRU','Даже у меня при мысли об этом топоре мурашки по коже, $n. Его могущество неоспоримо, но сам он – воплощение вечного голода.$B$BМы вынуждены воспользоваться им, потому что нам нужно выиграть эту войну любой ценой. И все же на твоем месте я был бы с ним крайне осторожен.','0');
