delete from `quest_offer_reward_locale` where `ID`=40650 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40650','ruRU','Я в раздумьях, $n. Я давно уже отложила свой дорожный посох – мне хотелось дожить свои дни в мире и покое, занимаясь обучением друидов.$B$BНо теперь, когда война пришла в мой дом, я не могу сидеть сложа руки.$B$BИ я, и мой посох – мы готовы отправиться в путь вместе с тобой, Рогатыйдруид.','0');
