insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('40647','ruRU','Я обязана тебе жизнью, $n. Если бы не ты, меня бы уже не было в живых... или и того хуже.$B$BИзменник Ксавий схватил Урсока, но, во всяком случае, Когти теперь у нас. Значит, надежда еще не потеряна.','0');
