insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39874','ruRU','Очень здорово, что ты пришел, $n. Сейчас такое время, что еще один зачаровыватель в лавке точно не помешает.','0');
delete from `quest_offer_reward_locale` where `ID`=39861 and `Locale`='ruRU';
