insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39731','ruRU','<Верховный маг Кадгар улыбается.>$B$BВ нынешние времена это большая редкость, $n, – но у меня для тебя хорошие новости.','0');
