insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39729','ruRU','Это... Это потрясающе! О таких технологиях я даже не догадывался! Отлично сработано, $n!','0');
