insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39651','ruRU','Спасибо, $n. Даже не знаю, что бы мы без тебя делали.$B$BВот, прими это в знак моей признательности.$B$BСчастливого празднования!','0');
