insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39519','ruRU','Ну что, $n, каково это? Посмотреть демону Пылающего Легиона в его налитые кровью глаза и сказать: \"Не сегодня\".$B$B<Ними запрокидывает голову, встряхивая копну рыжих волос, и звонко хохочет.>','0');
