insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39395','ruRU','Я очень рад, что ты доверился мне, $n. С моей души как камень упал.$B$BНо все же я еще не искупил своей вины перед всем Дренором за ту роль, которую сыграл в планах Гул\'дана. Мои сыновья помогут шаманам перепрятать фрагменты Кода. А я хочу помочь тебе расправиться с Гул\'даном раз и навсегда – если, конечно, ты позволишь.$B$BЯ буду верно служить тебе... командир.','0');
