insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39177','ruRU','Это ты приволок сюда это создание, $n? Оно бродило по нашему лагерю с таким сосредоточенным видом, что мы уж подумали, не шпионит ли оно за нами. Трое наших хранителей с трудом повязали его, но вроде бы оно не проявляет агрессии и готово оставаться у нас.','0');
