insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39057','ruRU','Прекрасно, $n. В доках кипит работа.$B$B<Кадгар прихлопывает в ладоши и потирает их друг о друга. В его бледно-голубых глазах мерцает что-то зловещее.>$B$BМы им покажем.','0');
