delete from `quest_offer_reward_locale` where `ID`=39033 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('39033','ruRU','Отличная работа, $n. Опытный предсказатель с помощью апекситовых кристаллов может отыскивать месторождения нефти, спрятанные глубоко под землей. К сожалению, обучить этому искусству тебя не получится, но ты все равно сможешь получить свою выгоду.','0');
