insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38956','ruRU','Приношу мои самые искренние извинения, $n. Я не только устроил скандал, но, кажется, еще и поставил под удар репутацию твоего ателье.$B$BТы имеешь полное право сердиться на меня.','0');
