insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38804','ruRU','Бедная Лирель.$B$BЯ не виню тебя в ее смерти, $n. Спасибо. Надеюсь, ты сделаешь то же и для меня, если я пойду по тому же пути.$B$BИ... постарайся извлечь урок из этой жуткой истории. Подумай о том, что произошло. Будет жаль, если ее жертва окажется напрасной.','0');
