insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38473','ruRU','Отрадно видеть тебя, $n. Седогрив отправил меня искать в этой деревне сведения об Эгиде. $B$BПока ничего выяснить не удалось – кроме того, что местные жители настроены весьма враждебно. Впрочем, когда бывало иначе?$B$BНадеюсь, тебе больше повезет в поисках.','0');
