insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38269','ruRU','А, $n, ты как раз вовремя.$B$BАктар рассказал мне, что Совет Теней начал обучать сетекков своим темным искусствам. Под знамена Совета Теней встали даже некоторые из высших араккоа!$B$BЯ все никак не возьму в толк, какую роль во всем этом играет Искар?','0');
