insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38253','ruRU','<Король внимательно изучает карту Танаанских джунглей и сопредельных областей.>$B$B$n! За этот гарнизон Альянс в огромном долгу перед тобой. Благодаря твоей смелости и готовности возглавить войско мы смогли вести эту войну здесь на Дреноре, а не в нашем родном мире, подвергая опасности своих родных и близких.$B$BПришло время покончить со всем этим. Ты готов к завершающей кампании?','0');
delete from `quest_offer_reward_locale` where `ID`=38246 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38246','ruRU','Спасибо, $n! Мой племянник вернулся ко мне.$B$BОн объяснил, что источником безумия был могучий тотем, спрятанный в нашей деревне. $B$BПохоже на правду, но кто – или ЧТО – подсунуло его нам?$B$BНужно как можно скорее рассказать обо всем этом друидам.','0');
