delete from `quest_offer_reward_locale` where `ID`=38223 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('38223','ruRU','Спасибо тебе, $n.$B$BЭто было непростое дело, и я снова очень благодарен тебе за помощь. Искар окончательно обезумел, и его нужно остановить.$B$BЯ надеюсь, ты будешь готов, когда пробьет решающий час.','0');
