delete from `quest_offer_reward_locale` where `ID`=37957 and `Locale`='ruRU';
insert into `quest_offer_reward_locale` (`ID`, `Locale`, `OfferRewardText`, `VerifiedBuild`) values('37957','ruRU','Держись от него подальше, $n.$B$BЭти падшие эльфы все одинаковые. Отвратительные, двуличные создания.','0');
